import { useState, useEffect} from 'react'

const App = () => {
  const [ value, setValue] = useState(null)
  const [ message, setMessage] = useState(null)

  const getMessages = async () => {
    const options = {
      method: "POST",
      body: JSON.stringify({
        message: value
      }),
      headers: {
        "Content-Type": "application/json"
      }
    }
    try {
      const response = await fetch('http://localhost:6969/completions', options)
      const data = await response.json()
      console.log(data)
      setMessage(data.choices[0].message)
    } catch (error) {
      console.error(error)
    }
  }

  console.log(value)

  return (
    <div className="gpt-app">
      <section className="side-bar">
        <button>+ New Chat</button>
        <ul className="history">
          <li>Common!</li>
        </ul>
        <nav>
          <p>With ❤️️ by @aimmore</p>
        </nav>
      </section>
      <section className="main">
        <h1 className="logo">AimMore GPT</h1>
        <ul className="feed">

        </ul>
        <div className="buttom-section">
          <div className="input-container">
            <input value={value}
              onChange={(e) => setValue(e.target.value)}
              placeholder='Enter a Text here'/>
            <div id="submit" onClick={getMessages}>➢ </div>
          </div>
          <p className="info">
            This is AI-enabled chat app, using OpenAI's GPT-3. 
            The API is liscened under OpenAI's API policy to @aimmore.
          </p> 
        </div>
      </section>
    </div>
  )
}

export default App
