import React, { useState, useEffect } from 'react'

const App = () => {
  const [value, setValue] = useState(null)
  const [message, setMessage] = useState(null)
  const [previousChats, setPreviousChats] = useState([])
  const [currentTitle, setCurrentTitle] = useState('')

  const getMessages = async () => {
    const options = {
      method: 'POST',
      body: JSON.stringify({
        message: value
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    }
    try {
      const response = await fetch('http://localhost:6969/completions', options)
      const data = await response.json()
      console.log(data)
      setMessage(data.choices[0].message)
    } catch (error) {
      console.error(error)
    }
  }

  useEffect(() => {
    console.log(currentTitle, value, message)
    if (!currentTitle && value && message) {
      setCurrentTitle(value)
    }
    if (currentTitle && value && message) {
      setPreviousChats(prevChats => [
        ...prevChats,
        {
          title: currentTitle,
          role: 'user',
          content: value
        },
        {
          title: currentTitle,
          role: message.role,
          content: message.content
        }
      ])
    }
  }, [currentTitle, value, message])

  console.log(previousChats)

  return (
    <div className="gpt-app">
      <section className="side-bar">
        <button>+ New Chat</button>
        <ul className="history">
          <li>Common!</li>
        </ul>
        <nav>
          <p>With ❤️️ by @aimmore</p>
        </nav>
      </section>
      <section className="main">
        {!currentTitle && <h1 className="logo">AimMore GPT</h1>}
        <ul className="feed"></ul>
        <div className="buttom-section">
          <div className="input-container">
            <input
              value={value}
              onChange={(e) => setValue(e.target.value)}
              placeholder="Enter a Text here"
            />
            <div id="submit" onClick={getMessages}>
              ➢
            </div>
          </div>
          <p className="info">
            This is AI-enabled chat app, using OpenAI's GPT-3.
            The API is licensed under OpenAI's API policy to @aimmore.
          </p>
        </div>
      </section>
    </div>
  )
}

export default App
